mapper.select:
SELECT rt.restaurant_id, rt.restaurant_name, rt.restaurant_address, rt.restaurant_lat, rt.restaurant_lng, rt.restaurant_category, ct.category_id, ct.category_name, rmct.category_count, (6371 * acos(cos(radians(#{lat})) * cos(radians(restaurant_lat)) * cos(radians(restaurant_lng) - radians(#{lng})) + sin(radians(#{lat})) * sin(radians(restaurant_lat)))) AS distance FROM restaurant_tb rt LEFT OUTER JOIN category_tb ct ON (ct.category_id = rt.restaurant_category) LEFT OUTER JOIN (SELECT rjt.category_id, count(*) as category_count FROM room_tb rt left outer join room_join_tb rjt ON(rjt.room_id = rt.room_id) WHERE rt.room_master_code = #{roomUrl} GROUP BY rjt.category_id) rmct ON(rmct.category_id = ct.category_id) WHERE 1=1
and restaurant_category IN (
#{categoryId}
)
HAVING 0.5 > distance;
